import { useEffect, useState } from 'react'
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

function App() {
  const [players, setPlayers] = useState([{
    "_id": "622793288e124becf34bff83",
    "player_name": "Tendulkar",
    "matches": 463,
    "inns": 452,
    "runs": 18426,
    "hs": 200,
    "ave": 44.83
    }])
  const [name, setName] = useState('')
  const playerList = players.map((player, index) => {
    <tr key={index}>
      <td>{player.player_name}</td>
      <td>{player.matches}</td>
      <td>{player.runs}</td>
      <td>{player.hs}</td>
    </tr>
  })

  const searchByName = async e => {
    console.log(playerList)
    let name = e.target.value;
    setName(name)
    if(name !== '') {
      let data = await axios.get(`http://localhost:5000/cricketer/${name}`)
      console.log(data.data.data != null)
      if(data.data.data != null)
        setPlayers([data.data.data])
    }
  }

  return (
    <div className='container'>
      <form>
        <label htmlFor="player-name">Player Name</label>
        <input type="text" value={name} onChange={searchByName} />
      </form>
      <table>
        <thead>
          <tr>
            <th>Player Name</th>
            <th>Matches</th>
            <th>Runs</th>
            <th>HS</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => {
    <tr key={index}>
      <td>{player.player_name}</td>
      <td>{player.matches}</td>
      <td>{player.runs}</td>
      <td>{player.hs}</td>
    </tr>
  })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
