import { useEffect, useState } from 'react'
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

function App() {
  const [players, setPlayers] = useState([])
  const [name, setName] = useState('')
  const [hs, setHs] = useState(null)

  useEffect(() => {
  }, []);

  const searchByName = async e => {
    let name = e.target.value;
    setName(name)
    if (name !== '') {
      let data = await axios.get(`http://localhost:5000/cricketer/${name}`)
      if (data.data.data != null)
        setPlayers([data.data.data])
    }
  }

  const searchByHs = async e => {
    let hs = e.target.value;
    setHs(hs)
    if (hs !== '') {
      let data = await axios.get(`http://localhost:5000/hs`, {
        headers: { 'content-type': 'application/json' },
        params: "Hello"
      })
      console.log(data)
      if (data.data.data != null)
        setPlayers([data.data.data])
    }
  }

  return (
    <div className='container'>
      <form>
        <label htmlFor="player-name">Player Name</label>
        <input type="text" id="player-name" value={name} onChange={searchByName} />
      </form>
      <form>
        <label htmlFor="player-hs">Player Hs</label>
        <input type="number" id="player-hs" value={hs} onChange={searchByHs} />
      </form>
      <table>
        <thead>
          <tr>
            <th>Player Name</th>
            <th>Matches</th>
            <th>Runs</th>
            <th>HS</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => {
            return <tr key={index}>
              <td>{player.player_name}</td>
              <td>{player.matches}</td>
              <td>{player.runs}</td>
              <td>{player.hs}</td>
            </tr>
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
